// sync-start:perseus-data-format-perseus-version 402776191 services/content-editing/perseus/perseus_data.go
export type Version = {|
    // The major part of the version
    major: number,
    // The minor part of the version
    minor: number,
|};
// sync-end:perseus-data-format-perseus-version

// sync-start:perseus-data-format-perseus-renderer 43032456 services/content-editing/perseus/perseus_data.go
export type PerseusRenderer = {|
    // Translatable Markdown content to be rendered.  May include references to
    // widgets (as [[â˜ƒ widgetName]]) or images (as ![image text](imageUrl)).
    // For each image found in this content, there can be an entry in the
    // `images` dict (below) with the key being the image's url which defines
    // additional attributes for the image.
    content: string,
    // A dictionary of {[widgetName]: Widget} to be referenced from the content field
    widgets: {|[string]: PerseusWidget|},
    // Used only for PerseusItem.hints.  If true, it replaces the previous hint in the list with the current one. This allows for hints that build upon each other.
    replace?: boolean,
    // Used in the PerseusGradedGroup widget.  A list of "tags" that are keys that represent other content in the system.  Not rendered to the user.
    // NOTE: perseus_data.go says this is required even though it isn't necessary.
    metadata?: $ReadOnlyArray<string>,
    // A dictionary of {[imageUrl]: PerseusImageDetail}.
    images: {|[string]: PerseusImageDetail|},
|};
// sync-end:perseus-data-format-perseus-renderer

